// Analyze date distribution
const dateStats = allStories.reduce((stats, story) => {
  const age = story.ageInDays;
  if (age <= 1) stats.today++;
  else if (age <= 3) stats.last3Days++;
  else if (age <= 7) stats.lastWeek++;
  else if (age <= 14) stats.last2Weeks++;
  else stats.older++;
  return stats;
}, { today: 0, last3Days: 0, lastWeek: 0, last2Weeks: 0, older: 0 });

