// Enhanced processing with better error handling
let content = $json?.message?.content ?? '';
if (typeof content !== 'string') content = String(content || '');

// Remove code fences if present
content = content.replace(/^```(?:json)?\s*/i, '').replace(/\s*```$/,'');

// Extract JSON from any surrounding text
const jsonStart = content.indexOf('{');
const jsonEnd = content.lastIndexOf('}');

if (jsonStart !== -1 && jsonEnd !== -1 && jsonEnd > jsonStart) {
  content = content.substring(jsonStart, jsonEnd + 1);
}

console.log('ðŸ”„ Processing AI response...');
console.log('ðŸ“ Content length:', content.length);
console.log('ðŸ‘€ First 300 chars:', content.substring(0, 300));
console.log('ðŸ”š Last 300 chars:', content.substring(Math.max(0, content.length - 300)));

let finalData;
try {
  finalData = JSON.parse(content);
  console.log('âœ… Successfully parsed JSON');
  console.log('ðŸ“Š Stories found:', finalData.stories?.length || 0);
  console.log('ðŸ“‚ Categories:', finalData.categories?.length || 0);

  // Validate the response has the expected structure
  if (!finalData.stories || !Array.isArray(finalData.stories)) {
    throw new Error('Missing or invalid stories array');
  }
  if (!finalData.categories || !Array.isArray(finalData.categories)) {
    throw new Error('Missing or invalid categories array');
  }

  // Post-AI deduplication - remove duplicate URLs
  const beforeDedup = finalData.stories.length;
  const seenUrls = new Set();
  const duplicates = [];

  finalData.stories = finalData.stories.filter((story, index) => {
    const url = story.url || '';
    if (!url) return true; // Keep stories without URLs

    if (seenUrls.has(url)) {
      duplicates.push({ index: index + 1, url, headline: story.headline });
      console.log(`ðŸ”„ Removed duplicate #${index + 1}: "${story.headline?.substring(0, 50)}..." (${url})`);
      return false;
    }

    seenUrls.add(url);
    return true;
  });

  if (duplicates.length > 0) {
    console.log(`ðŸ§¹ Deduplication: ${beforeDedup} â†’ ${finalData.stories.length} stories (removed ${duplicates.length} duplicates)`);
    duplicates.forEach(dup => {
      console.log(`  âŒ Duplicate story #${dup.index}: ${dup.headline?.substring(0, 60)}...`);
    });
  } else {
    console.log(`âœ… No duplicates found in AI response`);
  }

  // Update total_stories count after deduplication
  finalData.total_stories = finalData.stories.length;

} catch (error) {
  console.error('âŒ JSON parse failed:', error.message);
  console.log('ðŸ“„ Content length:', content.length, 'chars');
  console.log('ðŸ” Content ends with }?', content.trim().endsWith('}'));
  console.log('ðŸ“ Last 500 chars:', content.substring(Math.max(0, content.length - 500)));

  // Check if this is a truncation issue
  const isTruncated = !content.trim().endsWith('}') || error.message.includes('Unexpected end');

  // Fallback structure
  finalData = {
    title: 'Daily Cybersecurity Brief - Parse Error',
    total_stories: 1,
    categories: ['Error'],
    stories: [{
      category: 'Error',
      headline: 'AI Response Processing Failed',
      summary: isTruncated 
        ? `Response was truncated (${content.length} chars). Increase maxTokens from 16000 to 20000+ or reduce categories.`
        : `Invalid JSON: ${error.message}. The AI may have included explanatory text.`,
      why_matters: isTruncated
        ? 'Token limit reached before completing the response. Need to either increase token limit or simplify the prompt.'
        : 'The AI prompt needs adjustment to ensure valid JSON-only output.',
      companies: ['OpenAI'],
      url: '#',
      published_date: new Date().toISOString()
    }],
    metadata: {
