// Enhanced data preparation - realistic RSS feed timeframe
// Most RSS feeds only provide recent articles (7-14 days max)
// Use 14 days to capture more stories while being realistic
const now = new Date();
const daysBack = 14; // 2 weeks - more realistic for RSS feeds
const cutoff = now.getTime() - (daysBack * 24 * 60 * 60 * 1000);
const cutoffDate = new Date(cutoff);

console.log(`📅 RSS Feed Analysis - System Clock Time: ${now.toISOString()}`);
console.log(`🇸🇬 Singapore Time: ${now.toLocaleString('en-SG', {timeZone: 'Asia/Singapore'})}`);
console.log(`🌍 GMT Time: ${now.toUTCString()}`);
console.log(`🕐 Server Local Time: ${now.toLocaleString()}`);
console.log(`📅 Filtering stories from last ${daysBack} days: ${cutoffDate.toISOString()}`);
console.log(`🔍 Total input items received: ${$input.all().length}`);

// First, analyze what we're getting from RSS feeds
const allStories = $input.all().map((item, index) => {
  const j = item.json;
  const link = j.link || j.url || '';
  let host = 'unknown';
  try { 
    host = link ? new URL(link).hostname : 'unknown'; 
  } catch(e) {}

  // Ensure published date is in GMT/UTC format
  let publishedDate;
  const originalDate = j.isoDate || j.pubDate || j.date;
  try {
    publishedDate = new Date(originalDate || new Date()).toISOString();
  } catch(e) {
    publishedDate = new Date().toISOString();
  }

  const daysDiff = Math.floor((now.getTime() - new Date(publishedDate).getTime()) / (1000 * 60 * 60 * 24));
  console.log(`📄 Story ${index + 1}: "${(j.title || 'No title').substring(0, 40)}..." from ${host} - Age: ${daysDiff} days (${publishedDate})`);

  return {
    source: j.source || j.feed || host,
    title: j.title || 'No title',
    link: link,
    published: publishedDate,
    snippet: j.contentSnippet || j.description || j.summary || 'No description',
    categories: j.categories || [],
    ageInDays: daysDiff
  };
});

// Analyze date distribution
const dateStats = allStories.reduce((stats, story) => {
  const age = story.ageInDays;
  if (age <= 1) stats.today++;
  else if (age <= 3) stats.last3Days++;
  else if (age <= 7) stats.lastWeek++;
  else if (age <= 14) stats.last2Weeks++;
  else stats.older++;
  return stats;
}, { today: 0, last3Days: 0, lastWeek: 0, last2Weeks: 0, older: 0 });

console.log(`📊 Story age distribution:`);
console.log(`  📅 Today (0-1 days): ${dateStats.today}`);
console.log(`  📅 Last 3 days: ${dateStats.last3Days}`);
console.log(`  📅 Last week (4-7 days): ${dateStats.lastWeek}`);
console.log(`  📅 Last 2 weeks (8-14 days): ${dateStats.last2Weeks}`);
console.log(`  📅 Older than 2 weeks: ${dateStats.older}`);

console.log(`📊 Total stories before filtering: ${allStories.length}`);

// Filter with more lenient timeframe
const filteredStories = allStories.filter((story, index) => {
  try {
    const pubDate = new Date(story.published).getTime();
    const isRecent = pubDate >= cutoff;

    if (isRecent) {
      console.log(`✅ Keeping story ${index + 1}: "${story.title.substring(0, 40)}..." (${story.ageInDays} days old)`);
    } else {
      console.log(`🗑️ Filtering out story ${index + 1}: "${story.title.substring(0, 40)}..." (${story.ageInDays} days old - too old)`);
    }
    return isRecent;
  } catch(e) {
    console.log(`⚠️ Date parse error for story ${index + 1}: "${story.title.substring(0, 40)}...", keeping it`);
    return true; // Keep if we can't parse date
  }
});

console.log(`📰 Final result: ${filteredStories.length} stories from last ${daysBack} days for AI analysis`);
console.log(`📊 Date range: ${cutoffDate.toISOString()} to ${now.toISOString()}`);
console.log(`🎯 Cutoff: ${daysBack} days ago (${cutoffDate.toISOString()})`);

if (filteredStories.length === 0) {
  console.log(`⚠️ WARNING: No stories found! This suggests RSS feeds may not have recent content or there's a date parsing issue.`);
}

// Output ONE item that contains all stories (like original)
return [{ json: { stories: filteredStories } }];

// Enhanced processing with better error handling
let content = $json?.message?.content ?? '';
if (typeof content !== 'string') content = String(content || '');

// Remove code fences if present
content = content.replace(/^```(?:json)?\s*/i, '').replace(/\s*```$/,'');

// Extract JSON from any surrounding text
const jsonStart = content.indexOf('{');
const jsonEnd = content.lastIndexOf('}');

if (jsonStart !== -1 && jsonEnd !== -1 && jsonEnd > jsonStart) {
  content = content.substring(jsonStart, jsonEnd + 1);
}

console.log('🔄 Processing AI response...');
console.log('📏 Content length:', content.length);
console.log('👀 First 300 chars:', content.substring(0, 300));
console.log('🔚 Last 300 chars:', content.substring(Math.max(0, content.length - 300)));

let finalData;
try {
  finalData = JSON.parse(content);
  console.log('✅ Successfully parsed JSON');
  console.log('📊 Stories found:', finalData.stories?.length || 0);
  console.log('📂 Categories:', finalData.categories?.length || 0);

  // Validate the response has the expected structure
  if (!finalData.stories || !Array.isArray(finalData.stories)) {
    throw new Error('Missing or invalid stories array');
  }
  if (!finalData.categories || !Array.isArray(finalData.categories)) {
    throw new Error('Missing or invalid categories array');
  }

} catch (error) {
  console.error('❌ JSON parse failed:', error.message);
  console.log('📄 Content length:', content.length, 'chars');
  console.log('🔍 Content ends with }?', content.trim().endsWith('}'));
  console.log('📝 Last 500 chars:', content.substring(Math.max(0, content.length - 500)));

  // Check if this is a truncation issue
  const isTruncated = !content.trim().endsWith('}') || error.message.includes('Unexpected end');

  // Fallback structure
  finalData = {
    title: 'Daily Cybersecurity Brief - Parse Error',
    total_stories: 1,
    categories: ['Error'],
    stories: [{
      category: 'Error',
      headline: 'AI Response Processing Failed',
      summary: isTruncated 
        ? `Response was truncated (${content.length} chars). Increase maxTokens from 16000 to 20000+ or reduce categories.`
        : `Invalid JSON: ${error.message}. The AI may have included explanatory text.`,
      why_matters: isTruncated
        ? 'Token limit reached before completing the response. Need to either increase token limit or simplify the prompt.'
        : 'The AI prompt needs adjustment to ensure valid JSON-only output.',
      companies: ['OpenAI'],
      url: '#',
      published_date: new Date().toISOString()
    }],
    metadata: {
      source: 'Error Handler',
      ai_model: 'GPT-5',
      last_update: new Date().toISOString(),
      workflow_version: '6.0',
      error: error.message,
      content_length: content.length,
      truncated: isTruncated,
      last_100_chars: content.substring(Math.max(0, content.length - 100))
    }
  };
}

// Ensure required fields with GMT timestamps
finalData.generated_at = new Date().toISOString();
if (!finalData.stories) finalData.stories = [];
if (!finalData.categories) finalData.categories = [];
if (!finalData.metadata) finalData.metadata = {};

// Send data directly to webhook
console.log('🎣 Preparing data for webhook');
console.log('📊 Final summary:');
console.log('  Title:', finalData.title);
console.log('  Stories:', finalData.stories.length);
console.log('  Categories:', finalData.categories.join(', '));
console.log('  Generated:', finalData.generated_at);
console.log('🔗 Will send to webhook: /webhook/tech-brief');

// Add date for historical tracking
const currentDate = new Date().toISOString().split('T')[0];
finalData.date_key = currentDate;
finalData.singapore_date = new Date().toLocaleString('en-SG', {timeZone: 'Asia/Singapore', dateStyle: 'full'});

console.log('📅 Date key for history:', currentDate);
console.log('🇸🇬 Singapore date:', finalData.singapore_date);

return [{ json: finalData }];

